// Post.js
import MoreHorizIcon from "@mui/icons-material/MoreHoriz";
import { Avatar, TextField, Button } from "@mui/material";
import React, { useState } from "react";
import "./Post.css";
import FavoriteBorderIcon from "@mui/icons-material/FavoriteBorder";
import ChatBubbleOutlineIcon from "@mui/icons-material/ChatBubbleOutline";
import TelegramIcon from "@mui/icons-material/Telegram";
import BookmarkBorderIcon from "@mui/icons-material/BookmarkBorder";

function Post({ user, postImage, likes, timestamp, onLike }) {
  const [likeCount, setLikeCount] = useState(likes);
  const [isLiked, setIsLiked] = useState(false);
  const [comments, setComments] = useState([]);
  const [newComment, setNewComment] = useState("");

  const handleLike = () => {
    if (!isLiked) {
      setLikeCount(likeCount + 1);
      setIsLiked(true);
      onLike(); // Call the parent component's onLike function
    }
  };

  const handleShare = () => {
    // ... (same as previous implementation)
  };

  const handleCommentChange = (event) => {
    setNewComment(event.target.value);
  };

  const handleAddComment = () => {
    if (newComment.trim() !== "") {
      setComments([...comments, newComment]);
      setNewComment("");
    }
  };

  return (
    <div className="post">
      <div className="post__header">
        <div className="post__headerAuthor">
          <Avatar style={{ marginRight: "10px" }}>
            {user.charAt(0).toUpperCase()}
          </Avatar>{" "}
          {user} â€¢ <span>{timestamp}</span>
        </div>
        <MoreHorizIcon />
      </div>
      <div className="post__image" onClick={handleLike}>
        <img src={postImage} alt="Post Image" />
      </div>
      <div className="post__footer">
        <div className="post__footerIcons">
          <div className="post__iconsMain">
            <FavoriteBorderIcon
              className={`postIcon ${isLiked ? "liked" : ""}`}
            />
            <ChatBubbleOutlineIcon className="postIcon" />
            <TelegramIcon className="postIcon" onClick={handleShare} />
          </div>
          <div className="post__iconSave">
            <BookmarkBorderIcon className="postIcon" />
          </div>
        </div>
        Liked by {likeCount} people.
        {/* Display comments */}
        <div className="post__comments">
          {comments.map((comment, index) => (
            <div key={index} className="post__comment">
              <Avatar style={{ marginRight: "5px" }}>
                {user.charAt(0).toUpperCase()}
              </Avatar>
              <span>{comment}</span>
            </div>
          ))}
        </div>
        {/* Comment input */}
        <div className="post__commentBox">
          <TextField
            fullWidth
            variant="outlined"
            placeholder="Add a comment..."
            value={newComment}
            onChange={handleCommentChange}
          />
          <Button variant="contained" onClick={handleAddComment}>
            Add
          </Button>
        </div>
      </div>
    </div>
  );
}

export default Post;
